float dft(float y)
{
    float c = 0.;
    float s = 0.;
    float f = pow(2.,5.*y)*10.;
    for (int i = 0; i< 512; i++){
        float x = float(i)/512.;
        s += (texture(iChannel0, vec2(x,1.)).x-.5)*sin(x*f);
        c += (texture(iChannel0, vec2(x,1.)).x-.5)*cos(x*f);    
    }
    return sqrt(c*c+s*s);


}



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 pixelSize = 1. / iResolution.xy;
    vec2 uv = fragCoord.xy * pixelSize;

    if(uv.x > 1. - pixelSize.x){

        float c= log(1.+2.*dft(uv.y));
        //c += texture(iChannel0,  vec2(pow(2.,5.*(uv.y-0.06))/64.,0.)).x*2.;
        fragColor = vec4(c*c*.2,c*.3,c*.8,0.)*.5; 
        //fragColor = texture(iChannel0,  vec2(uv.y,0.));

    }else{
        fragColor = texture(iChannel1, uv + vec2(1,0) * pixelSize);
    }
}
