void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Correct time and resolution to start at a more interesting moment
    float multiplier = iResolution.x / 512.0;
    float ntime = 20.0 + iTime;

    // Normalized pixel coordinates with a fixed aspect ratio
    vec2 uv = fragCoord / iResolution.y;
    uv *= multiplier;
    uv += atan(ntime * multiplier + 100.);

    // Time varying pixel color
    vec3 col = vec3(
        sin(dot(uv, uv) * 10000.0)
    );

    // Output to screen
    fragColor = vec4(col,1.0);
}
