float f1(float x, float t) { return sin(x+t)*sin(x/5.-t*4.); }
float f2(float x, float t) { return f1(x/2.,t/2.)*1.3-f1(x-1.,t-5.); }
float f3(float x, float t) { return f2(x/2.,t/2.)*1.3-f1(x-1.,t-5.); }
float f4(float x, float t) { return f3(x,t)*(0.5+f1(x/3.4-3.,t*2.-5.)/2.); }

vec3 col(float x) {
  x /= 4.;
  return x > 0. ? vec3(
      clamp(x, 0., 1./3.),
      clamp(x-1./3., 0., 1./3.),
      clamp(x-2./3., 0., 1./3.)
   ) * 3.
   :  vec3(
      clamp(-x-2./3., 0., 1./3.),
      clamp(-x-1./3., 0., 1./3.),
      clamp(-x, 0., 1./3.)
   ) * 3.
   ;
}

void mainImage( out vec4 O, in vec2 I )
{
    vec2 uvu = I/max(iResolution.x, iResolution.y) * 10.;
    O = vec4(col(f4(uvu.x, iTime) + f4(uvu.y + 42.123, iTime/1.321+31.32)), 0);
}
