#define ChromaOffset 0.01

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;

    vec2 Offset = texture(iChannel1, vec2(iTime*22.0)).rg;
    Offset.x *= 0.02; Offset.y *= 0.04;
    vec4 video = texture(iChannel0, uv);

video.r = texture(iChannel0, (uv+Offset.x - ChromaOffset)).r;
  video.g = texture(iChannel0, uv+Offset).g;
    video.b = texture(iChannel0, uv+Offset.y + ChromaOffset).b;

    fragColor = video;
}
