// based on https://www.shadertoy.com/view/MslyWB

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Scale mouse and fragment, and move to center of screen.
    vec2 m = (iMouse.xy - .5 * iResolution.xy) / iResolution.y;
	fragCoord = (fragCoord - .5 * iResolution.xy) / iResolution.y;
    fragCoord -= m.xy;

    // Constants
	float pi = 3.14;
    float thickness = 6.;
    float curve = 3.;

     // Spiral
     float len = length(fragCoord);
    float theta = atan(fragCoord.y, fragCoord.x);   
    float spin = 2. * (fract(iTime)*0.5)+0.5;

    float r1 = fract(curve * theta / pi + thickness * len - spin);
    float g1 = fract(curve * theta / pi + thickness*0.5 * len - spin);
    float b1 = fract(curve * theta / pi + thickness*0.25 * len - spin);

    // Make each side of the "bands" symetrical (no hard edges)
    float r2 = r1>0.5?1.0-r1:r1;
    float g2 = g1>0.5?1.0-g1:g1;
    float b2 = b1>0.5?1.0-b1:b1;

    // Fade out towards the edges of the screen.
    float r = (1.-len)*r2;
    float g = (1.-len)*g2;
    float b = (1.-len)*b2;


    fragColor= vec4 (r, g, b, 0.);
}
