const float TwoPI = radians(360.0);

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    float unit = min(iResolution.x, iResolution.y) * 0.5;

    vec2 center = iResolution.xy / 2.0;
    float scale = sin(iTime * 2.0) * 0.5 + 0.5;
    vec2 dir = center - fragCoord;

    float rot = (1.0 - fract(iTime * 0.5)) * TwoPI;
    float ang = mod(atan(dir.x, dir.y) + radians(180.0) + rot, TwoPI) * 1.2;
    ang *= float(ang <= TwoPI);

    float intsy = ang / TwoPI;
    vec2 bounds = vec2(0.43 * unit, 0.55 * unit) * (0.3 * scale + 1.0);
    float pixel = dot(step(length(dir), bounds), vec2(0.0, 1.0));

    fragColor = vec4(vec3(intsy * pixel), 1.0);
}
