const vec3 BLACK = vec3(0);
const vec3 GREEN = vec3(0,1,0);

void seperate(float val, out float wholepart, out float fractpart)
{
    wholepart = floor(val);
    fractpart = fract(val);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
  vec2 st = (fragCoord.xy - .5 * iResolution.xy) /iResolution.yy;
  float ar = iResolution.x / iResolution.y;

  vec3 color = GREEN;
  float w = .05 / (ar);
  float h = .05;

  float rectsW, offsetW;
  seperate(st.x / w * .5, rectsW, offsetW);

  float rectsH, offsetH;
  seperate(st.y / h * .5, rectsH, offsetH);


  float stageX = mod(rectsW, 6.);
  float stageY = mod(rectsH, 2.);

  float stage = mod(stageX + stageY * 3.0, 6.); //every other row is offset by half the period.

  vec3 top_color = color;
  top_color = mix(BLACK,top_color,smoothstep(.01,.03,offsetH));

  vec3 bot_color = color;
  bot_color = mix(BLACK,bot_color,smoothstep(.01,.03,1.0 - offsetH));

  vec3 slash_color = color;
  slash_color = mix(BLACK,slash_color,smoothstep(.02,.06,abs(offsetH - offsetW)));

  vec3 bslash_color = color;
  bslash_color = mix(BLACK, bslash_color,smoothstep(.02,.06,abs((1.0 - offsetH) - offsetW)));

  color = slash_color;

  color = mix(top_color,color,step(0., stage - 5.));
  color = mix(top_color,color,step(0., stage - 4.));
  color = mix(bslash_color,color, step(0., stage - 3.));
  color = mix(bot_color,color, step(0., stage - 2.));
  color = mix(bot_color,color, step(0., stage - 1.));

  fragColor = vec4(color,1.0);
}
