void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 U = fragCoord/iResolution.xy;

    vec3 col;

    vec3 A = vec3(0., 0., 1.);
    vec3 B = vec3(0., 1., 1.);
    vec3 C = vec3(1., 0., 1.);
    vec3 D = vec3(1., 0., 0.);

    for(int i = 0; i < max(int(mod(iTime, 8.)), 1); ++i) {
        //check if it is in the L shaped portion of [0,1] x [0,1]
        if(U.x < .5 || U.y < .5) {
            if(U.y >= .5) {

                //[0, 0.5] x [0.5, 1] => [0,1] x [0,1]
                U = (U - vec2(0., .5)) * 2.;

                if(U.x >= .5 && U.y < .5) {
                    //shift to form top of Red L shape
                    U += vec2(-.5, .5);
                    col = D;    
                } else {
                    //flip in y direction
                    U.y = 1. - U.y;
                    col = A;
                }

            } else if(U.x >= .5) {
                //[0.5, 1.] x [0.0, 0.5] => [0,1] x [0,1]
                U = (U - vec2(.5, 0.)) * 2.;

                if(U.x < .5 && U.y >= .5) {
                    //shift to form right of Red L shape
                    U += vec2(.5, -.5); 
                    col = D;
                } else {
                    //flip in x direction
                    U.x = 1. - U.x;
                    col = B;
                }
            } else {
                //[0.0, 0.5] x [0.0, 0.5] => [0,1] x [0,1]
                U *= 2.; 

                if(U.x >= .5 && U.y >= .5) {
                    //shift to form butt of Red L shape
                    U += vec2(-.5, -.5); 
                    col = D;
                } else {
                    col = C;
                }
            }
        } else {
            col = vec3(0.);
        } 

    }


    // Output to screen
    fragColor = vec4(col,1.0);
}
