// rework og this GLSL Shader https://glslsandbox.com/e#76901.0
#ifdef GL_ES
precision mediump float;
#endif



float rand(float seed) {
	seed = mod(seed * 61.6, 12.38) + 5.16;
	return fract(71524.1 * sin(pow(seed, 5.)));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){
	vec2 r = iResolution.xy;
	vec2 p = fragCoord.xy / r;

	// Sky gradient

	vec3 sky = vec3(0., .0, p.y*1.);


	vec3 col = sky;

	// Rows of buildings
	for (int i = 5; i >= 2; i--) {
		float row = float(i*2);
		float which_building = p.x * (8.2 + pow(row, 1.3) * 4.) + iTime * 4.0 / (1.6 + row) + 712.9 * row;
		float height = rand(floor(which_building)) * pow(0.9, row+1.) + row * 0.05 - .1;
		float building_x = pow(which_building,5.);
		float building_y = (height - p.y) * row -2.;
		if (building_y > 0.) {
			// Hit building
			col = sky * pow(0.9, 23.*abs(sin(iTime)) - row);
			// Lights
			float rlight = rand(floor(which_building + building_y * 0.)); // This glitched out but I like it now.
			if (abs(building_x - 0.5) < 0.4 && fract(building_y * 4.) > 0.2)
				col += vec3(0.3, 0.1, .2) * pow(0.8, row) * rlight;

		}
	}

	bool b = false;
	p = gl_FragCoord.xy - vec2(3., 4.);

	// Lettera C
	if (distance(p, vec2(50., 30.)) < 20.) b = true;
	if (distance(p, vec2(50., 30.)) < 15.) b = false;
	if (distance(p, vec2(50., 65.)) < 20.) b = true;
	if (distance(p, vec2(50., 65.)) < 15.) b = false;
	//if (p.x > 50. && abs(p.y - 50.) < 12.) b = false;

	if (p.x < 40.) b=false;

	if (abs(p.x - 40.) < 5.) b = true;

	// Le due stanghette verticali I e T
	//if (min(abs(p.x - 110.), abs(p.x - 160.)) < 5.) b = true;

	// stanghetta orizzontale T
	//if (abs(p.x - 160.) < 30. && p.y > 80.) b = true;


	//if (abs(p.x - 230.) < 5. && p.y < 50.) b = true;

	//if (min(abs(p.y - 2. * p.x + 416.), abs(p.y + 2. * p.x - 504.)) < 9. && p.y > 42.) b = true;

	if (p.y < 10. || p.y > 85.) b = false;


	if (b) col = vec3(1.0);
	fragColor = vec4(col, 0.7);
}
