float generalized_checker(vec2 v, float a, float b, float c)
{
    return mod(dot(floor(v), vec2(a, b)), c);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = fragCoord;
    p *= .1f; // Scale
    // Generalized Checker at a = 1, b = 1 and c = 2 is just ordinary checker
    float v = generalized_checker(p, 1.0f, 1.0f, 2.0f);
    fragColor.rgb = vec3(v);
    fragColor.a = 1.0f;
}
