mat2 rotate(float a){
    return mat2(cos(a), -sin(a),
                sin(a), cos(a));
}

float circle(float radius, vec2 position, vec2 uv){
    float d = distance(uv, position)/radius;
    //float mask = clamp(floor(d), 0., 1.);
    float mask = step(floor(d), 0.);
    return mask;
}

float drawRect(vec4 rect, vec2 uv){
    vec2 hv = step(rect.xy, uv) * step(uv, rect.zw);
    float a = hv.x * hv.y;
    return a;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = (fragCoord.xy-0.5*iResolution.xy)/iResolution.y;
    float A = 3., B = 2.;
    uv *= A;

    vec3 col = vec3(1.);
    float x,y;
    float uvRad = atan(uv.y, uv.x);
    float rad2 = atan(1., -1.5);
    float rad4 = atan(-1., 1.5);

    float r=0., b=0.;
    float c = circle(B/2./2., vec2(0., 0.), uv);

    if(uvRad < rad2 && uvRad > rad4){
        r = c;
    }else{
        b = c;
    }

    x = B/4./2. * cos(rad2);
    y = B/4./2. * sin(rad2);

    c = circle(B/4./2., vec2(x, y), uv);
    r = max(r, c);
    b -= c;

    c = circle(B/4./2., vec2(-x, -y), uv);
    r -= c;
    b = max(b, c);

    col *= vec3(r, 0, b);

    // background
    float f = drawRect(vec4(-1.5, -1., 1.5, 1.), uv);
    col = mix(col, vec3(1.), step(length(col), 0.)*f);

    // draw rectangle
    // 3
    float tmpx;
    vec2 uv2 = uv;
    uv2 = rotate(rad4) * uv;
    tmpx = -(B/4.+B/8.+B/24.);
    f = drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., B/4./2.), uv2);
    tmpx += -(B/48.+B/24.);
    f += drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., B/4./2.), uv2);
    tmpx += -(B/48.+B/24.);
    f += drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., B/4./2.), uv2);
    col = mix(col, vec3(0.), f);

    // 4
    uv2 = rotate(-rad4) * uv;
    tmpx = -(B/4.+B/8.+B/24.);
    f = drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., B/4./2.), uv2);
    tmpx += -(B/48.+B/24.);
    f += drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., -B/48./2.), uv2);
    f += drawRect(vec4(tmpx, B/48./2., tmpx+B/24., B/4./2.), uv2);
    tmpx += -(B/48.+B/24.);
    f += drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., B/4./2.), uv2);
    col = mix(col, vec3(0.), f);

    // 5
    uv2 = rotate(-rad2) * uv;
    tmpx = -(B/4.+B/8.+B/24.);
    f = drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., -B/48./2.), uv2);
    f += drawRect(vec4(tmpx, B/48./2., tmpx+B/24., B/4./2.), uv2);
    tmpx += -(B/48.+B/24.);
    f += drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., B/4./2.), uv2);
    tmpx += -(B/48.+B/24.);
    f += drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., -B/48./2.), uv2);
    f += drawRect(vec4(tmpx, B/48./2., tmpx+B/24., B/4./2.), uv2);
    col = mix(col, vec3(0.), f);

    // 6
    uv2 = rotate(rad2) * uv;
    tmpx = -(B/4.+B/8.+B/24.);
    f = drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., -B/48./2.), uv2);
    f += drawRect(vec4(tmpx, B/48./2., tmpx+B/24., B/4./2.), uv2);
    tmpx += -(B/48.+B/24.);
    f += drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., -B/48./2.), uv2);
    f += drawRect(vec4(tmpx, B/48./2., tmpx+B/24., B/4./2.), uv2);
    tmpx += -(B/48.+B/24.);
    f += drawRect(vec4(tmpx, -B/4./2., tmpx+B/24., -B/48./2.), uv2);
    f += drawRect(vec4(tmpx, B/48./2., tmpx+B/24., B/4./2.), uv2);
    col = mix(col, vec3(0.), f);

    fragColor = vec4(col, 1.0);
}
