void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float l;
    vec3 c;
    vec2 uv = (fragCoord - .5 * iResolution.xy) / iResolution.y;

    for(int i=0; i<3; i++) {
      l = length(uv);
      uv += sin(l*9.-(iTime + 0.07 * float(i))*2.);
      c[i]=.01 / length(uv);
    }
    fragColor = vec4(c/l, 1.0);
}
