void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    const vec4 color=vec4(0.8,0.8,0.81,1.0);

    const vec4 background=vec4(0.92,0.93,0.94,1.);

    const float lineWidth=1.;

    const vec2 offset=vec2(0.,0.);

    const vec4 lightColor=vec4(1.);

    const float lightRange=50.;

    float margin=125.+cos(float(iTime))*120.;

    float x=step(mod(abs(fragCoord.x-floor(iResolution.x*0.5)-offset.x),margin),lineWidth);

    float y=step(mod(abs(fragCoord.y-floor(iResolution.y*0.5)-offset.y),margin),lineWidth);

    float m=1.-smoothstep(distance(iMouse.xy,fragCoord.xy),0.0,lightRange);    

    fragColor=mix(background,color+lightColor*m,clamp(x+y,0.,1.));
}
