void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
  float dotResolution = 50.;
  vec2 colorUV = fragCoord / iResolution.xy;
  vec2 uv = fragCoord / iResolution.y;
  vec4 color = texture(iChannel0, colorUV);

  // RGB-to-CMYK conversion
  vec4 cmyk;
  cmyk.xyz = 1.0 - color.rgb;
  cmyk.w = min(cmyk.x, min(cmyk.y, cmyk.z));
  cmyk.xyz -= cmyk.w;

  vec2 Kst = dotResolution*mat2(0.707, -0.707, 0.707, 0.707)*uv;
  vec2 Kuv = 2.0*fract(Kst)-1.0;
  float kR = sqrt(cmyk.w);
  float k = smoothstep(kR + 0.1, kR - 0.1, length(Kuv));
  vec2 Cst = dotResolution*mat2(0.966, -0.259, 0.259, 0.966)*uv;
  vec2 Cuv = 2.0*fract(Cst)-1.0;
  float cR = sqrt(cmyk.x);
  float c = smoothstep(cR + 0.1, cR - 0.1, length(Cuv));
  vec2 Mst = dotResolution*mat2(0.966, 0.259, -0.259, 0.966)*uv;
  vec2 Muv = 2.0*fract(Mst)-1.0;
  float mR = sqrt(cmyk.y);
  float m = smoothstep(mR + 0.1, mR - 0.1, length(Muv));
  vec2 Yst = dotResolution*uv; // 0 deg
  vec2 Yuv = 2.0*fract(Yst)-1.0;
  float yR = sqrt(cmyk.z);
  float y = smoothstep(yR + 0.1, yR - 0.1, length(Yuv));

  vec3 rgb = 1. - vec3(c, m, y);
  rgb = mix(rgb, vec3(0., 0., 0.), k);
  fragColor = vec4(rgb, 1.0);
}
