void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float gridResolution = 10.0;

    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Create a grid of (gridResolution x gridResolution) cells that go from 0 -> 1
    vec2 uv2 = fract(uv * gridResolution);

    // Convert the grid dimensions from 0 -> 1 to -1 -> 1
    // Dimensions that center on 0 make it easier to draw circles.
    vec2 uv3 = 2.0 * uv2 - 1.0;

    fragColor = vec4(uv2.x, uv2.y, 0.0, 1.0);
}
