// HexTexturedSpiral.glsl  2021-03-26

#define R iResolution
#define T iTime

// counter clockwise rotation
vec2 rotate2d (vec2 pos, float angle)
{ float ca = cos(angle),  sa = sin(angle);
  return pos * mat2(ca, sa, -sa, ca);  
}

void mainImage(out vec4 O, in vec2 U )
{
  vec2 uv = (U.xy - 0.5*R.xy) / R.y;
  vec2 p = uv;
  float i=0., g=0., d=2.;
  for(float j=0.; j<44.; j++) 
  {
	if (d<=.001) break;
    ++i;
    p = uv*g - rotate2d(vec2(.3), g*2.);
    d = -(length(p) -2. + g/9.) / 2.;
    g += d;
  }
  p = vec2(atan(p.x,p.y),g)*8.28 - T;
  p = abs(fract(p + vec2(0,.5*ceil(p.x)))-.5);
  float s = 30./i-.5/step(.9,1.-abs(max(p.x*1.5+p.y,p.y*2.)-1.));
  s = clamp(s*0.3, 0., 1.);
  O = texture(iChannel0, p.xy) + vec4(s);
}
