// acid test simplification of "GLSL bug: side effects" by ttg. https://shadertoy.com/view/WlyBR3


void mainImage( out vec4 O, vec2 U )
{
    U -= .5;     // just to get the exact same pattern than https://www.shadertoy.com/view/WlyBR3
    U *= .01;
    float a = U.x , b = U.y,    
          p1 = (1.+a)*(1.+b)-(1.+a+b), // is equivalent to a*b, but order introduce small differences
       // p1 = a*b,                    // ← try this one: should exactly = p2, but no !
          p2 = a*b;

    O = vec4(0);
    O.r = float( abs(p1-p2) < 1e-10 ); // expected precision error: black when p1 and p2 differs, otherwise red. 

 // O.r = float( abs( a*b - a*b ) < 1e-10 ); // ← but this one should always be true... and is not !
 // O.r = float( a*b == a*b  );              // ← plus it is inconsistant with this

 // O.g = -p2;                        // ← uncommenting this changes the red channel ! ( with 1st def of O.r )
}
