// even more simplified version of https://shadertoy.com/view/NdlSDB
// acid test simplification of https://shadertoy.com/view/WlyBR3

void mainImage( out vec4 O, vec2 U )
{
    U -= .5 + iResolution.xy/2.;
    U *= .01;

    O = vec4( abs( U.x*U.y  - U.x*U.y ) < 1e-10 );  
 // O = vec4(      U.x*U.y == U.x*U.y );             // differs from above
}
