// Signed distance to a sdSpiral
//-------------------------------------------------------------------
// inspired by 
// DEL          - SpiralExtrudeTest - https://www.shadertoy.com/view/NdfcD4
// Tater        - Spiraled Layer    - https://www.shadertoy.com/view/Ns3XWf
// sylvain69780 - SDF for Achimedean Spiral - https://www.shadertoy.com/view/stB3WK
//-------------------------------------------------------------------

#define PI 3.1415926535


float sdCap(vec2 p, vec2 a, vec2 b) {
    vec2 pa = p - a, ba = b - a;
    return length( pa - ba*clamp( dot(pa,ba)/dot(ba,ba), 0., 1. ));
}

float sdSpiralSquare(in vec2 p, in float r, in float e, float st, float en)  {
    float d = length(p.xy)/r + .5*atan(p.x,p.y)/PI;  
    d = r*abs(d - clamp(round(d), st, en))-e;
    p.y = abs(p.y+.5*r*(en+st)) - .5*r*(en-st+1.);
    return min(d, sdCap(p, vec2(0,e), vec2(0,-e))); // cap squere
}

float sdSpiralRound(in vec2 p, in float r, in float e, float st, float en) {
    float d = length(p.xy)/r + .5*atan(p.x,p.y)/PI;  
    d = r*abs(d - clamp(round(d), st, en))-e;
    p.y = abs(p.y+.5*r*(en+st)) - .5*r*(en-st+1.);
    return min(d, length(p) - e);  // cap round
}    


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
	vec2 p = (2.0*fragCoord-iResolution.xy)/iResolution.y,
         m = (2.0*iMouse.xy-iResolution.xy)/iResolution.y;

    int start = 1+int(iTime*.25)%3, end = start + 1+ int(iTime)%3;
    float r = .5/float(end),
          ep = r*.5*(.5+.5*cos(.4*iTime));

    float d;
    if (mod(iTime, 4.) < 1.)
         d = sdSpiralRound(p,r,ep, float(start), float(end));
    else d = sdSpiralSquare(p,r,ep, float(start), float(end));

    // coloring
    vec3 col = vec3(1) - sign(d)*vec3(.1,.4,.7);
    col *= 1. - exp(-3.*abs(d));
	col *= .8 + 0.2*cos(150.*d);
	col = mix(col, vec3(1), smoothstep(.01,0.,abs(d)) );

    if( iMouse.z>.001 )
    {
        if (mod(iTime, 4.) < 1.)
             d = sdSpiralRound(m,r,ep, float(start), float(end));
        else d = sdSpiralSquare(m,r,ep, float(start), float(end));

        col = mix(col, vec3(1,1,0), smoothstep(.005, 0., abs(length(p-m)-abs(d))-.0025));
        col = mix(col, vec3(1,1,0), smoothstep(.005, 0., length(p-m)-.015));
    }

	fragColor = vec4(col,1.);
}
