#define H(h) (cos((h)*6.3+vec3(0,23,21))*.5+.5)

const float maxd=80.0;

vec3 rot(vec3 p, vec3 axis,float theta){
	axis=normalize(axis);
	return mix(axis*dot(p,axis),p,cos(theta))+sin(theta)*cross(p,axis);
}

#if 1
float modifiedAbs(float x){
    float k= 1.;
    k=exp(k*x);
    return (x*k-x/k)/(k+1./k);
    /*
    float k= 1.;
    float h=tanh(x*k);
    return h*h*abs(x);
    */
}

#else
float modifiedAbs(float x){
    float k=1e-2;
    return sqrt(x*x+k);
}

#endif

float modifiedMin(float a, float b){
    return (a+b-modifiedAbs(a-b))*.5;
}

float modifiedMax(float a, float b){
    return (a+b+modifiedAbs(a-b))*.5;
}

vec2 modifiedFold(vec2 p, vec2 v)
{
    float g=dot(p,v);
    return p-(g-modifiedAbs(g))*v;
}

// inspired https://twitter.com/7CIT/status/1235459606840610816
vec2 signedModifiedFold(inout vec2 p, vec2 v)
{
    float g=dot(p,v);
    return (p-(g-modifiedAbs(g))*v)*vec2(sign(g),1);
}

vec2 mirrorVic(float a)
{
    a=radians(a)*.5;
    return vec2(cos(a),sin(a));
}

float sdRoundBox( vec3 p, vec3 b, float r )
{
  vec3 q = abs(p) - b;
  return length(max(q,0.0)) + min(max(q.x,max(q.y,q.z)),0.0) - r;
}

float map(vec3 p)
{
    float de0=p.y+2.2;
    p=rot(p,normalize(vec3(0,1,0)),iTime*.5);
    vec3 q,v;
    q=p;
    float a=iTime;
    v=vec3(cos(a),sin(a),0);
    q-= clamp(dot(q,v), 0., 2.) * v;
    float de1=length(q)-.5;
    q=p;
    v=normalize(vec3(-1,0,.5));
    q-= clamp(dot(q,v), 0., 2.) * v;
    float de2=length(q)-.5;
    return min(de0,modifiedMin(de1,de2));
}

vec3 calcNormal(vec3 p)
{
  vec3 n=vec3(0);
  for(int i=0; i<4; i++){
    vec3 e=.001*(vec3(9>>i&1, i>>1&1, i&1)*2.-1.);
    n+=e*map(p+e);
  }
  return normalize(n);
}

float march(vec3 ro, vec3 rd, float near, float far)
{
    float t=near,d;
    for(int i=0;i<100;i++)
    {
        t+=d=map(ro+rd*t);
        if (d<0.001) return t;
        if (t>=far) return far;
    }
    return far;
}

float calcShadow( vec3 light, vec3 ld, float len ) {
	float depth = march( light, ld, 0.0, len );	
	return step( len - depth, 0.01 );
}

vec3 doColor(vec3 p)
{
     return mix(vec3(1),H(.95),.6);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{  
    vec2 uv = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec3 ro = vec3(2.5);
    vec3 ta = vec3(0);
    vec3 rd = normalize(vec3(uv,2.0));
    vec3 w = normalize(ta-ro);
    vec3 u = normalize(cross(w,vec3(0,1,0)));
    vec3 v = cross(u,w);
    rd = mat3(u,v,w) * rd;
    vec3 col= vec3(0.05,0.05,0.1);
    float t=march(ro,rd,0.0,maxd);
    if(t<maxd)
    {
        vec3 p=ro+rd*t;
        col=doColor(p); 
    	vec3 n = calcNormal(p);      
		vec3 lightPos=vec3(5,5,1);
    	vec3 li = lightPos - p;
		float len = length( li );
		li /= len;
		float dif = clamp(dot(n, li), 0.0, 1.0);
        float sha = min(1.,calcShadow( lightPos, -li, len )+.8);
        col *= max(sha*dif, 0.2);
        float rimd = pow(clamp(1.0 - dot(reflect(-li, n), -rd), 0.0, 1.0), 2.5);
		float frn = rimd+2.2*(1.0-rimd);
    	col *= frn;
    	col*=max(.5+.5*n.y,.3);
        col*=exp2(-2.*pow(max(0.,1.-map(p+n*.8)/.8),2.));
        col+=vec3(.3,.3,.3)*pow(clamp(dot(reflect(rd,n),li),0.,1.),80.);
    }
    fragColor.xyz = col;
}
