float PI = 3.14,
      R = .8,
      n = 14.,
      m = 10.,
      z = 10.;
vec2 c = vec2(-.8, .4);

void mainImage( out vec4 O, in vec2 u )
{
    vec2 Res = iResolution.xy, 
        U = z * ( 2. * u - Res ) / Res.y;   

    float SR = .2;
    float S = 0.03;
    float r = distance(U, z * c);
    vec3 star = vec3(min(exp((-r + R)/.05), 1.) * abs(1./SR));

    for(float i = 1.; i < m; i++) {
        float Rd = R * SR * i;
        float R = R * i;
        for(float j = 0.; j < n; j++) {
            float a = R * (j+1.) * (2. * PI)/(n) + iTime/10.;
            r = distance(U - z * c, Rd * vec2(-sin(a) + 1.7, cos(a) - 1.7));
            O += vec4(min(exp((-r + R)/.02), 1.)) * S * SR;
        }
    }

    O+= vec4(star, 1.);
}
