#define iTime (5.0 * iTime)
#define TAU 6.2831853072

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord / iResolution.y - vec2(0.0, 0.5);
    float unit = 2.0 / iResolution.y;

    float zoom = mod(10.0 * iTime, TAU);
    float y = uv.x * cos(10.0 * log(uv.x) - zoom) / 10.0;

    float diff = exp(uv.x * 4.0);
    float fade = exp(-uv.x * 2.0);

    fragColor = vec4(1.0 - smoothstep(unit * diff, 0.0, abs(uv.y - y)) * fade);
}
