#define PI 3.14159265359

vec3 mod289(vec3 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec2 mod289(vec2 x) { return x - floor(x * (1.0 / 289.0)) * 289.0; }
vec3 permute(vec3 x) { return mod289(((x*34.0)+1.0)*x); }


mat2 rotate2d_(float a)
{
    float s = sin(a);
    float c = cos(a);

    return mat2(c, -s, s, c);
}

float snoise(vec2 v) {

    // Precompute values for skewed triangular grid
    const vec4 C = vec4(0.211324865405187,
                        // (3.0-sqrt(3.0))/6.0
                        0.366025403784439,
                        // 0.5*(sqrt(3.0)-1.0)
                        -0.577350269189626,
                        // -1.0 + 2.0 * C.x
                        0.024390243902439);
                        // 1.0 / 41.0

    // First corner (x0)
    vec2 i  = floor(v + dot(v, C.yy));
    vec2 x0 = v - i + dot(i, C.xx);

    // Other two corners (x1, x2)
    vec2 i1 = vec2(0.0);
    i1 = (x0.x > x0.y)? vec2(1.0, 0.0):vec2(0.0, 1.0);
    vec2 x1 = x0.xy + C.xx - i1;
    vec2 x2 = x0.xy + C.zz;

    // Do some permutations to avoid
    // truncation effects in permutation
    i = mod289(i);
    vec3 p = permute(
            permute( i.y + vec3(0.0, i1.y, 1.0))
                + i.x + vec3(0.0, i1.x, 1.0 ));

    vec3 m = max(0.5 - vec3(
                        dot(x0,x0),
                        dot(x1,x1),
                        dot(x2,x2)
                        ), 0.0);

    m = m*m ;
    m = m*m ;

    // Gradients:
    //  41 pts uniformly over a line, mapped onto a diamond
    //  The ring size 17*17 = 289 is close to a multiple
    //      of 41 (41*7 = 287)

    vec3 x = 2.0 * fract(p * C.www) - 1.0;
    vec3 h = abs(x) - 0.5;
    vec3 ox = floor(x + 0.5);
    vec3 a0 = x - ox;

    // Normalise gradients implicitly by scaling m
    // Approximation of: m *= inversesqrt(a0*a0 + h*h);
    m *= 1.79284291400159 - 0.85373472095314 * (a0*a0+h*h);

    // Compute final noise value at P
    vec3 g = vec3(0.0);
    g.x  = a0.x  * x0.x  + h.x  * x0.y;
    g.yz = a0.yz * vec2(x1.x,x2.x) + h.yz * vec2(x1.y,x2.y);
    return 130.0 * dot(m, g);
}

float Circle(vec2 uv, float radius, float blur)
{
    return mix(0.,1.,smoothstep(radius, radius-blur, length(uv)));

}

vec2 CartToPolar(vec2 uv, float rotation, float distortion, float rotating)
{
   vec2 st = vec2(atan(uv.x, uv.y), length(uv));
   uv = vec2(st.x/6.2831+.5 +(iTime*rotating) *.1+ (st.y*distortion) ,st.y);

   return uv;
}


float PolarForm(vec2 uv, float smooth1, float size1, float size2, 
                float numSide, float rotation, float distortion, float rotating)
{
    vec2 st = CartToPolar(uv, rotation, distortion, rotating);
    float x = st.x * numSide;
    float m = min(fract(x), fract(1.-x));
    float c = smoothstep(0., smooth1, m*size1+size2-st.y);
    return c;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;
    uv -= 0.5;
    uv.x *= iResolution.x / iResolution.y;
    uv *= 50.;

    float distX = tan(iTime)-sin(iTime);
    float distY = cos(iTime)*sin(iTime)*20.;

    vec2 i_uv = floor(uv);
    vec2 f_uv = fract(uv);
    float dist = distance(i_uv, vec2(distX, distY))*(sin(iTime*.2)*.5+.5)*.2;
    vec3 text = texture(iChannel0, i_uv).rgb;

    float c = 0.;
    float radius = 0.;
    float blur = snoise(i_uv);

    for(float x=-2.; x<=2.; x++){
        for(float y=-2.; y<=2.; y++){
            vec2 offset = vec2(x,y);
            vec2 coord = f_uv - .5;
            coord *= offset*0.2;
            radius = sin(dist);

            if (dist < sin(iTime)*.5+.5)
                c += smoothstep(radius,radius-blur, length(coord*2.));

        }
    }


    // Time varying pixel color
    vec3 col = vec3(0.);
    col += (1.-c/text)*.9;
    // Output to screen
    fragColor = vec4(col,1.0);
}
