#define PI 3.14159265359

#define drawSDF(d, c) color = mix(color, c, smoothstep(unit, 0.0, d))
#define drawFunction(f, d, t, c) color = mix(color, c, smoothstep(unit, 0.0, abs(uv.y - f(uv.x)) / sqrt(1.0 + pow(d(uv.x), 2.0)) - t))

// Parabola and derivative for DE
float f1(in float x) {
    return x * x / (3.0 * PI) - 3.0 * PI / 4.0;
}

float f1Prime(in float x) {
    return 2.0 * x / (3.0 * PI) ;
}

// Composition of the parabola and cosine wave
float f2(in float x) {
    return mix(f1(x), cos(x), step(0.0, abs(x) - 3.0 * PI / 2.0));
}

float f2Prime(in float x) {
    return mix(f1Prime(x), -sin(x), step(0.0, abs(x) - 3.0 * PI / 2.0));
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = (fragCoord - 0.5 * iResolution.xy) / iResolution.y * 16.0;
    float unit = 32.0 / iResolution.y;
    vec3 color = vec3(1.0);

    // Grid
    drawSDF(abs(fract(uv.x + 0.5) - 0.5) + 0.25 * unit, vec3(0.0, 0.0, 1.0));
    drawSDF(abs(fract(uv.y + 0.5) - 0.5) + 0.25 * unit, vec3(0.0, 0.0, 1.0));
    drawSDF(abs(uv.x), vec3(1.0, 0.0, 0.0));
    drawSDF(abs(uv.y), vec3(1.0, 0.0, 0.0));

    drawFunction(cos, -sin, 0.25 * unit, vec3(0.0, 0.0, 1.0));
    drawFunction(f1, f1Prime, 0.25 * unit, vec3(1.0, 0.0, 0.0));
    drawFunction(f2, f2Prime, 0.25 * unit, vec3(0.5, 0.0, 0.5));

    fragColor = vec4(color, 1.0);
}
