// vec4(Red, Green, Blue, Alpha)
const vec4 Color1 = vec4(1.0, 0.0, 0.0, 1.0);
const vec4 Color2 = vec4(0.0, 1.0, 0.0, 0.1);
const vec4 Color3 = vec4(0.0, 0.0, 1.0, 1.0);
const vec4 Color4 = vec4(1.0, 0.0, 1.0, 0.5);

const vec4 ColorPos = vec4(0.1, 0.45, 0.8, 0.9);


void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 ScreenUV = fragCoord/iResolution.xy;
    float TexSample0 = texture(iChannel0, ScreenUV).r;

#if 0
    TexSample0 = TexSample0 + fract(iTime*0.333);
    if (TexSample0 > 1.0) {TexSample0 = TexSample0 - 1.0;}
#endif

    // Color mix value:  (TextureValue - ColorPos.Start)  /  ( Distance between ColorPos.Start and ColorPos.End )
    vec4 TheColor;
    if      (TexSample0 < ColorPos.x) { TheColor = Color1; }
    else if (TexSample0 < ColorPos.y) { TheColor = mix( Color1, Color2, (TexSample0 - ColorPos.x)/(ColorPos.y - ColorPos.x) ); }
    else if (TexSample0 < ColorPos.z) { TheColor = mix( Color2, Color3, (TexSample0 - ColorPos.y)/(ColorPos.z - ColorPos.y) ); }
    else if (TexSample0 < ColorPos.w) { TheColor = mix( Color3, Color4, (TexSample0 - ColorPos.z)/(ColorPos.w - ColorPos.z) ); }
    else                              { TheColor = Color4; }

    vec4 TexSample1 = texture(iChannel1, ScreenUV);

    fragColor = vec4( mix(TexSample1.rgb, TheColor.rgb, TheColor.a), 1.0 );
}
