// Colorization and output stuff here.
void mainImage( out vec4 fragColor, in vec2 fragCoord) {    
    fragColor.rgb = tanh(texelFetch(iChannel0, ivec2(fragCoord), 0).zzz * 50.0);
}

#define GET_X(d) texelFetch(iChannel0, ivec2(mod(fragCoord + vec2(d) + res, res)), 0).x
#define GET_Y(d) texelFetch(iChannel0, ivec2(mod(fragCoord + vec2(d) + res, res)), 0).y
#define GET_Z(d) texelFetch(iChannel0, ivec2(mod(fragCoord + vec2(d) + res, res)), 0).z

// How fast particle tail decays.
const float TAIL_DECAY = 0.99;

// Probability for a particle to spawn at initialization stage.
const float PARTICLE_PROB = 0.0008;

// When tail decays under this value, it's set to zero.
const float EPS = 0.001;

float noise(in vec2 co){
    float a = fract(co.x * 10.5 + co.y * 7.5 + fract(iDate.a));
    a = fract(715.5 * a * a + 57.1 * co.x);
    a = fract(1371.5 * a * a + 757.1 * co.y);
    return a;
}


// Main CA computation
void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 res = iResolution.xy;

    if (iFrame > 0) {
    	ivec2 d;

        // x component contains tail value
        // y component contains angle of a particle
        //   (zero means no particle is present)
        // z component measures decay of the tail.
        float val = GET_Y(vec2(0.0, 0.0));

        if (abs(val) > 0.0) { // Current cell contains a particle
            // A particle moves and leaves tail each step
            fragColor.x = val;
            fragColor.y = 0.0;
            // Newly left tail has decay level of 1.0
            fragColor.z = 1.0;
        } else {
            // Old tails decay each step
            fragColor.z = GET_Z(vec2(0.0, 0.0)) * TAIL_DECAY;
            // Tail value propagates to next iteration
            fragColor.x = GET_X(vec2(0.0, 0.0));

            // Tails with too much decay become zero
            if (fragColor.z < EPS){
                fragColor.z = 0.0;
                fragColor.x = 0.0;
            }


            // Check if there's a particle movintg towards this cell
            for(d.y = -1; d.y <= +1; d.y++){
                for(d.x = -1; d.x <= +1; d.x++){
                    val = GET_Y(d);
                    // If such particle is found, this cell becomes a partincle on next iteration
                    if (abs(val) > 0.0 && d == ivec2(round(sin(val)), round(cos(val)))){
                        fragColor.y = val + 57.1 * fract(0.01 * fragColor.x) + 0.01 * fract(val);
                    }
                }
            }
        }
    } else {
        // Seeding.
        if (noise(fragCoord * 0.01) > 1.0 - PARTICLE_PROB)
            fragColor = vec4(0.0, 10.0 * noise(fragCoord), 1, 0.0);
        else {
            fragColor = vec4(0.0, 0.0, 0.0, 0.0);

        }
    }
}
