void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from -1 to 1)
    vec2 uv = 1.0-2.0*fragCoord/iResolution.xy;

    vec3 col=vec3(1.0);
    vec3 face=vec3(1.0, 0.85, 0.6);
    vec3 hat1_1=vec3(1.0, 0.0, 0.2);
    vec3 hat1_2=vec3(1.0, 0.0, 0.2);
    vec3 hat1_3=vec3(1.0, 0.0, 0.2);
    vec3 hat2=vec3(1.0, 0.0, 0.2);
    vec3 hat3=vec3(1.0, 0.0, 0.2);
    vec3 neck=vec3(1.0, 0.85, 0.6);
    vec3 body1=vec3(1.0, 0.0, 0.2);
    vec3 body2=vec3(1.0, 1.0, 1.0);
    vec3 body3=vec3(1.0, 1.0, 1.0);
    vec3 cheeks=vec3(0.9, 0.4, 0.3);
    vec3 eye1=vec3(0.0, 0.0, 0.0);
    vec3 eye2=vec3(1.0, 0.85, 0.6);
    vec3 nose=vec3(1.0, 0.85, 0.6);
    vec3 mouth1=vec3(0.0, 0.0, 0.0);
    vec3 mouth2=vec3(1.0, 0.85, 0.6);

     float  x1=uv.x-0.2; //face
     float  y1=uv.y+0.05;

     float  x8=uv.x-0.2; //neck
     float  y8=uv.y-0.2;

     float  x2=uv.x-0.2; //hat1_1
     float  y2=uv.y+0.26;

     float  x3=uv.x-0.32; //hat1_2
     float  y3=uv.y+0.26;

     float  x4=uv.x-0.08; //hat1_3
     float  y4=uv.y+0.26;

     float  x5=uv.x-0.2; //hat2
     float  y5=uv.y+0.85;

     float  x7=uv.x-0.2; //hat3
     float  y7=uv.y+0.8;

     float  x16=uv.x-0.11; //mouth1
     float  y16=uv.y-0.1;

     float  x17=uv.x-0.105; //mouth2
     float  y17=uv.y-0.085;

     float  x9=uv.x-0.2; //body1
     float  y9=uv.y-0.73;

     float  x10=uv.x-0.055; //body2
     float  y10=uv.y-0.63;

     float  x11=uv.x-0.08; //body3
     float  y11=uv.y-0.47;

     float  x12=uv.x-0.19; //cheeks
     float  y12=uv.y-0.065;

     float  x13=uv.x-0.12; //eye1
     float  y13=uv.y+0.08;

     float  x14=uv.x-0.12; //eye2
     float  y14=uv.y+0.097;

     float  x15=uv.x+0.4; //nose

     float  y15=uv.y+0.03+ abs(0.02 * sin(iTime));


    if (x1*x1+y1*y1/3.0<0.15*0.15) {
      col=face;
    }
    if ((abs(x8)<0.03)&&(abs(y8)<0.17)) {
      col=neck;
    }
    if ((abs(x2)<0.12)&&(abs(y2)<0.06)) {
      col=hat1_1;
    }

    if (y15<(x15/10.0)&&y15>(x15/(-10.0))&&x15<0.6) {
      col=nose;
    }
    if (y5>x5*4.5&&y5>x5*(-4.5)&&y5<0.6) {
      col=hat2;
    }
    float rad1=0.034;
     if (x3*x3+y3*y3/3.0<rad1*rad1) {
       col=hat1_2;
     }
      if (x4*x4+y4*y4/3.0<rad1*rad1) {
        col=hat1_3;
      }
       rad1=0.04;
        if (x7*x7+y7*y7/3.0<rad1*rad1) {
          col=hat3;
        }

    float rad2=0.25;
     if (x9*x9*2.5+y9*y9/3.0<rad2*rad2&&y9<0.1) {
       col=body1;
     }
      rad2=0.03;
       if (x10*x10+y10*y10/3.0<rad2*rad2) {
         col=body2;
       }
        if (x11*x11+y11*y11/3.0<rad2*rad2) {
          col=body3;
        }
    float rad3=0.04;
      if (x12*x12+y12*y12/3.0<rad3*rad3) {
        col=cheeks;
      }
       rad3=0.03;
        if (x13*x13+y13*y13/3.0<rad3*rad3) {
          col=eye1;
        } 
         if (x14*x14+y14*y14/3.0<rad3*rad3) {
           col=eye2;
         } 
          rad3=0.02;
           if (x16*x16+y16*y16/3.0<rad3*rad3) {
             col=mouth1;
           } 
            if (x17*x17+y17*y17/3.0<rad3*rad3) {
              col=mouth2;
            } 
    // Output to screen
    fragColor = vec4(col,1.0);
}
