#define tau 6.283185307
#define pi tau/2.
#define sharp 0
#define arms 1.

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    float m = max(iResolution.x, iResolution.y);
    vec2 uv = 0.5 * (fragCoord.xy - (0.5 * iResolution.xy)) / m;
    // uv = uv * 2.0 - vec2(1.0);

    // rt is (r, Î¸) but both normalized [0,1]
    vec2 rt = vec2(length(uv),atan(uv.y, uv.x)/tau + 0.5);
    float a = 10.0 * cos(iTime/2.);
    float b = iTime/3.;
    float c = sin(iTime);
    float d = mod((arms * rt.y + b) + rt.x * a, 1.0);
#if sharp
    if (d < 0.5) {
        d = 0.;
    } else {
        d = 1.;
    }
#endif
    fragColor = vec4(
        d * d, 
        0., 
        (1. - d) * (1. -d), 
        1.
    );
}
