const int deg = 3;
vec2 roots[deg];

vec2 mul(vec2 a, vec2 b) {
    return vec2(
        a.x*b.x - a.y*b.y,
        a.x*b.y + a.y*b.y
    );
}

vec2 div(vec2 a, vec2 b) {
    return mul(a, vec2(b.x, -b.y))/(b.x*b.x+b.y*b.y);
}

vec2 inv(vec2 a) {
    return vec2(a.x, -a.y) / (a.x*a.x + a.y*a.y);
}

vec2 f(vec2 a) {
    vec2 ret = vec2(1.0, 0.0);
    for (int i = 0; i < deg; i++) {
        ret = mul(ret, a-roots[i]);
    }
    return ret;
}

vec2 fp(vec2 a) {
    vec2 sum = vec2(0.0, 0.0);
    for (int i = 0; i < deg; i++) {
        sum += inv(a-roots[i]);
    }
    return inv(sum);
}

vec4 col(vec2 a) {
    return vec4(1.0/(1.0+0.1*abs(a.x)), 1.0/(1.0+0.21*abs(a.y)), 0.0, 1.0);
}

void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    roots[0] = vec2(cos(0.6*iTime), sin(0.3*iTime));
    roots[1] = vec2(cos(0.4*iTime), sin(0.5*iTime));
    roots[2] = vec2(cos(0.1*iTime), sin(0.1*iTime));
    vec2 u = 2.0*(fragCoord-iResolution.xy/2.0)/min(iResolution.x, iResolution.y);
    for(int i = 0; i < 3; i++) {
        u -= div(f(u), fp(u));
    }
    fragColor = col(u);
}
