void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // UVs (from -1 to 1)
    vec2 uv = (fragCoord-.5*iResolution.xy)/iResolution.y;
    float timeDis=sin(1.25*iTime)*.6+.6;
    float s=sin(1.25*iTime+timeDis);
    float c=cos(1.25*iTime+timeDis);
    uv*=mat2(c,s,-s,c);

    //Radial UVs    
    vec2 ruv = vec2(length(uv),atan(uv.y,uv.x))+(s*.3)+.5;

    // Spiral and color
    vec3 col = vec3(smoothstep(.505,.5,ruv.x+ruv.y*c*.1));
    col*=vec3(-uv.x*2.,ruv+uv.y*.5);

    // Output to screen
    fragColor = vec4(col,1.0);
}
