const float blurPower = 0.01;
const uint sampleCount = 6u;



void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // placement of the blur center
    float time = iTime * 0.5;
    vec2 center = vec2(sin(time), cos(time)) * 0.5 + 0.5;


	vec2 direction = uv - center;
	vec3 c = vec3(0.0, 0.0, 0.0);

    float f = 1.0 / float(sampleCount);

    for(uint i = 0u; i < sampleCount; ++i)
    {
		c += texture(iChannel0, uv - blurPower * direction * float(i)).rgb * f;
	}

    // Output to screen
    fragColor = vec4(c,1.0);
}
