bool circle(vec2 p, vec2 c, float r)
{
 float scaleY = iResolution.y / iResolution.x;
    return (p.x - c.x) * (p.x - c.x) + (p.y   - c.y) * (p.y - c.y) * (scaleY * scaleY) < r*r; 
}

