void mainImage(out vec4 color, in vec2 coord) {
    vec2 uv = coord / iResolution.xy;
    color = texture(iChannel0, uv);    
    color.r = 0.5+0.5*sin(8.*color.r);
    color.g = 0.5+0.5*sin(8.*color.g);
    color.b = 0.5+0.5*sin(8.*color.b);
}

float trail(vec2 uv, float r) {
    vec2 p = 0.5 + r * vec2(cos(64.*iTime*r), sin(34.*iTime*r));
    float d = distance(uv, p);
    return clamp(.1-5.*d, 0., 1.);
}

void mainImage(out vec4 color, in vec2 coord) {
    vec2 uv = coord / iResolution.xy;
    color = 0.99*texture(iChannel0, 0.99*(uv-0.5)+0.5);
    color.rgb += trail(uv, 0.03) * vec3(1.5, 0.5, 0.5*(0.5+0.2*sin(iTime)));
    color.rgb += trail(uv, 0.05) * vec3(0.2, 0.8, 0.7+0.2*cos(iTime));
}
