void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float sc = mix(0.25, 0.1, tanh(iTime));
    vec3 col = texelFetch(iChannel0, ivec2(sc *fragCoord), 0).rgb;

    fragColor = vec4(col,1.0);
}

float pxLine(vec2 f, vec2 p, vec2 q) {
    f -= p;

    vec2 dir = 0.5 * (q - p);

    float d = min(abs(f.x - round(dir.x/dir.y * f.y)),
                  abs(f.y - round(dir.y/dir.x * f.x)));

    return step(d, 0.) * step(abs(f.x - dir.x), abs(dir.x))
                       * step(abs(f.y - dir.y), abs(dir.y));           
}

