void mainImage( out vec4 fragColor, in vec2 f )
{
    vec3 col = vec3(0);

    // fragCoord must be offset by 0.5
    f -= vec2(30,20) - 0.5;

    // pxLine must take rounded positions as inputs 
    vec2 p = round(10. * vec2(cos(iTime), sin(1.25 * iTime)));
    vec2 q = round(14. * vec2(cos(1.15 * iTime), sin(iTime)));

    // Draw the origin + points
    col   += step(length(f), 0.);
    col.r += step(length(f - p), 0.);
    col.g += step(length(f - q), 0.);

    // Draw the line
    col.b += pxLine(f, p, q);      

    fragColor = vec4(col,1.0);
}
