void mainImage(out vec4 fragColor, in vec2 fragCoord) {
    vec2 uv = fragCoord.xy / iResolution.xy;
    vec4 originalColor = texture(iChannel0, uv);   
    vec4 sourceColor = texture(iChannel0, uv);


//https://twitter.com/iquilezles/status/1440847977560494084?s=20
    originalColor.r = pow(originalColor.r, 3.0/2.0);
    originalColor.g = pow(originalColor.g, 4.0/5.0);
    originalColor.b = pow(originalColor.b, 3.0/2.0);


    fragColor = mix(sourceColor, originalColor, step(iMouse.x/iResolution.x, uv.x));
}
