// https://gl-transitions.com/editor/randomsquares
// Easy and nice

vec2 size = vec2(10,10);
float smoothness = 0.5; // 0-1

float rand (vec2 co) {
  return fract(sin(dot(co.xy ,vec2(12.9898,78.233))) * 43758.5453);
}


void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
   vec2 uv = fragCoord/iResolution.xy;
   float r = rand(floor(vec2(size) * uv));
   float j;
   float m = smoothstep(0.0, -smoothness, r - (modf(iTime/2.,j) * (1.0 + smoothness)));
   fragColor=  mix(texture(iChannel0,uv), texture(iChannel1,uv), m);
}
