vec3 linColor(float col, vec2 uv)
{
    col = mod(col * 6.0, 6.0);
    vec3 color;
    color.r = 1.0 - clamp(col - 1.0, 0.0, 1.0) + clamp(col - 4.0, 0.0, 1.0);
    color.g = clamp(col, 0.0, 1.0) - clamp(col - 2.0, 0.0, 1.0);
    color.b = clamp(col- 2.0, 0.0, 1.0) - clamp(col - 5.0, 0.0, 1.0);

    return color + 0.3*cos(iTime+uv.xyx+vec3(0,2,4));
}


void mainImage(out vec4 fragColor, in vec2 fragCoord)
{
    vec2 uv = (2.*fragCoord - iResolution.xy) / iResolution.y;

    float len = length(uv);
    float angle = atan(uv.y, uv.x)*3.;

    float dist = .2;
    dist= 1. / sqrt(dist);
    float powLen = (1.0 / sqrt(len))*10.;
    float ring = abs(sin(powLen * 16.0 * dist+ angle - iTime * 22.));

    vec3 col = linColor(powLen * dist * dist * dist, uv)*ring;
    fragColor = vec4(col, 1.0) ;
}
