#define WAVE_AMPLITUDE 0.1
#define WAVE_FREQ 3.0
#define WAVE_SPEED 2.0
#define DISTORTION_ROTATION 4.0
#define GRID_SIZE 10.0

mat2 rot(float a) {
    float s = sin(a);
    float c = cos(a);
    return mat2(c, -s, s, c);
}
float waveX(vec2 uv, float offs, float scale) {
    float wav = sin(uv.x*scale+offs);
    return wav;
}
float waveY(vec2 uv, float offs, float scale) {
    vec2 ruv = uv * rot(3.1415/2.0);
    float wav = waveX(ruv, offs, scale);
    return wav;
}
vec3 uvGrid(vec2 uv, vec3 gridCol) {
    float gri = (floor(fract(uv.x*GRID_SIZE)+0.05)+
    floor(fract(uv.y*GRID_SIZE)+0.05));
    vec3 col = vec3(gridCol) * gri;
    return col;
}
vec3 gridCol(vec2 uv, float offs) {
    vec3 col = vec3(
    waveX(uv*rot(3.1415/(+1.0)),offs*(WAVE_SPEED/20.0), WAVE_FREQ/1.0)+1.0,
    waveX(uv*rot(3.1415/(-2.0)),offs*(WAVE_SPEED/30.0), WAVE_FREQ/1.0)+1.0,
    waveY(uv*rot(3.1415/(-2.0)),offs*(WAVE_SPEED/10.0), WAVE_FREQ/1.0)+1.0
    )*vec3(1.0, 1.0, 1.0);
    return col*vec3(0.5, 0.1, 1.0);
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = ((fragCoord-0.5*iResolution.xy)/iResolution.y);
    vec3 col = vec3(0.0,0.0,0.0);
    vec2 distUV = vec2(
        (uv.x + waveY(uv*rot(3.1415/DISTORTION_ROTATION),
        iTime*(WAVE_SPEED-0.741), WAVE_FREQ)*WAVE_AMPLITUDE), 
        (uv.y + waveX(uv*rot(3.1415/DISTORTION_ROTATION),
        iTime*WAVE_SPEED, WAVE_FREQ)*WAVE_AMPLITUDE)
    );
    col += uvGrid(distUV, gridCol(distUV, iTime));
    fragColor = vec4(col, 1.0);
}
