vec2 rotZ(vec2 p, float a) {
    float c = cos(a);
    float s = sin(a);
    return mat2(c, -s, s, c) * p;
}

float draw(vec2 uv) {
    float f = texture(iChannel0, vec2(0.)).x;
    float R = mod(abs(sin(length(10. * uv) - 5. * f - 5. * iTime)), 2.);
    float d = f * .05/length(uv) + mix(.25, 0.05, sin(iTime))/abs(R);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = (2. * fragCoord - iResolution.xy)/iResolution.y;
    uv = rotZ(uv, iTime);
    float z = cos(iTime/10.) * 50.;
    uv = round(uv*z)/z;
    fragColor = vec4(draw(uv), 0., 0., 1.) +
                vec4(0., draw(uv + 0.5), 0., 0.) +
                vec4(0., 0., draw(uv - vec2(0.5, -0.5)), 1.);
}
