vec3 hsl2rgb( in vec3 c )
{
    vec3 rgb = clamp( abs(mod(c.x*6.0+vec3(0.0,4.0,2.0),6.0)-3.0)-1.0, 0.0, 1.0 );
    return c.z + c.y * (rgb-0.5)*(1.0-abs(2.0*c.z-1.0));
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{

    float timePeriod = 20.0;
    float wavePeriod = 25.0;
    float width = 10.0;
    vec2 screenCoord = fragCoord - iResolution.xy/2.0;
    vec2 uv =  width * screenCoord / min(iResolution.x, iResolution.y);


    float x = uv.x;
    float y = uv.y;
    float z = 0.0;
    z = wavePeriod * mod(iTime,timePeriod)/timePeriod;
    float d = sin(x)*cos(y) + sin(y)*cos(z) + sin(z)*cos(x);
    // diamond
    //d = sin(x)*sin(y)*sin(z)+sin(x)*cos(y)*cos(z)+cos(x)*sin(y)*cos(z)+cos(x)*cos(y)*sin(z);
    //Schwartz
    //d = cos(x)+cos(y)+cos(z);



    float h = (d + 2.0)/4.0;
    float s = 0.5;
    float l = 0.5;

    if(0.49<h&&h<0.51){l=0.0;};

    vec3 col = hsl2rgb(vec3(h,s,l));

    // Output to screen
    fragColor = vec4(col,1.0);
}
