float RandFast( vec2 PixelPos)
{
    vec2 Magic = vec2(3571.0, 3571.0);
	vec2 Random2 = ( 1.0 / 4320.0 ) * PixelPos + vec2( 0.25, 0.0 );
	float Random = fract( dot( Random2 * Random2, Magic ) );
	Random = fract( Random * Random * (2.0 * 3571.0) );
	return Random;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord.xy / iResolution.xy;
    float rnd = RandFast(fragCoord.xy);
    rnd = step(rnd, 1.2*iMouse.x / iResolution.x-0.1);
    vec3 col0 = texture(iChannel0, uv).rgb;
    vec3 col1 = texture(iChannel1, uv).rgb;
    fragColor = vec4(mix(col0, col1, rnd), 1.0);
}
