// Noise function from my other post https://www.shadertoy.com/view/fdKBDV
#define N(P) (P.x * 676769 ^ P.y * 53769) * 676769

void mainImage(out vec4 C, in vec2 U) {
	ivec2 I = ivec2(U / 8. + iTime * 4.),
        J = I & 1;

    C = vec4(
        J.x & J.y |
		(J.x ^ J.y) * N(I) // Corridors
    );
}

