//A simple animation for this tweet https://twitter.com/jose_morval/status/1402595056582791169

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 uv = fragCoord/iResolution.xy;
    uv-=0.5;
    uv.x*=iResolution.x/iResolution.y;
    uv*=1.-0.5*fract(0.1*iTime);
    vec3 col = vec3(0.);
    float mask = 2.*smoothstep(0.1,0.,length(uv));
    mask += 5.*smoothstep(0.05,0.03,length(uv));
    mask += 1.*smoothstep(0.1,0.,length(uv)-0.5*fract(0.5*iTime)+0.2)*(1.-(fract(0.5*iTime)));

    col = mask*vec3(1.,0.5,0.15);


    float t = fract(0.1*iTime);

    float angle = t/0.1+exp(5.*t);
    mask = 1.*smoothstep(0.05,0.,length(uv-0.5*exp(-3.*t)*vec2(2.*cos(angle)-0.5,sin(angle))));
    mask += 5.*smoothstep(0.01,0.,length(uv-0.5*exp(-3.*t)*vec2(2.*cos(angle)-0.5,sin(angle))));

    col += mask*vec3(0.5,0.7,1.);

    col += 2.*smoothstep(0.95,0.99,t)*vec3(1.,0.5,0.5);
    col += (1.-step(abs(uv.y)-smoothstep(0.02,0.1,t),0.))*vec3(1.,1.,1.);


    fragColor = vec4(col,1.0);
}
