vec3 BGcolor(vec2 uv, vec3 col){
int i;
    float j;
    j=0.0;
    float r;
    r=(uv.x-0.5)*(uv.x-0.5)+(uv.y-0.5)*(uv.y-0.5);
    for(i=0;i<25;i++){
        if((r > j*0.02)&&(r < (j+1.0)*0.02)){
            if(i%3==0){
                col.r=j*0.02;
                if(i>0){
                    col.r=(j*0.02-abs(cos(iTime*0.4))*j*0.02)*2.0;
                }
            }
            else if(i%3==1){
                col.g=(j*0.02-abs(cos(iTime*0.4))*j*0.02)*2.0;
            }
            else{
                col.b=(j*0.02-abs(cos(iTime*0.4))*j*0.02)*2.0;
            }
        }
        j+=1.0;
    }
return col;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = fragCoord/iResolution.xy;

    // Time varying pixel color
    vec3 col;// = 0.5 + 0.5*cos(iTime+uv.xyx+vec3(0,2,4));
    //col.r=(uv.x-0.5)*(uv.x-0.5)+(uv.y-0.5)*(uv.y-0.5);
    //col.g=((uv.x-0.5)*(uv.x-0.5)+(uv.y-0.5)*(uv.y-0.5))*cos(iTime);
    //col.b=((uv.x-0.5)*(uv.x-0.5)+(uv.y-0.5)*(uv.y-0.5))*sin(iTime);
    col=BGcolor(uv,col);
    // Output to screen
    fragColor = vec4(col,1.0);
}
