#define RADIUS 0.025
#define SPACING 0.001

float circlei(vec2 po, vec2 pi, float r)
{
    return max(r - length(pi - po), .0);
}

float map(vec2 po, vec2 pm)
{
    float d = .0;
    d += circlei(po, pm, RADIUS);
    return d;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    vec2 p = (fragCoord * 2.0 - iResolution.xy) / iResolution.y;
    vec2 sp = vec2((abs(mod(p.x, RADIUS * 2.0 + SPACING * 2.0)) - RADIUS - SPACING), p.y);
    vec2 uv = fragCoord/iResolution.xy;
    float d = map(sp, vec2(0.0,0.0)) / 0.05;
    //d = smoothstep(0.0, 0.05, d);
    vec3 col = vec3(.0);

    vec2 sp2 = vec2((abs(mod(p.x, 0.25)) - 0.125) / 0.25, p.y / 0.2);


    col += vec3(d, d, d);

    // Output to screen
    fragColor = vec4(col,1.0);
}
