#define N 10
#define W 4.5f
#define S 0.1f

void mainImage( out vec4 fragColor, in vec2 fragCoord ) {
    vec2 uv = fragCoord/iResolution.xy;
    vec2 mid = 15.0f*vec2(iResolution.x/iResolution.y * (2.0f*uv.x-1.0f), 2.0f*uv.y-1.0f);

    vec3 col = vec3(0.4f, 0.8f, 0.3f);

    vec2[N] points;

    for (int i = 1; i <= N; i++) {
        points[i-1] = float(i)*vec2(sin(float(i+1)*S*iTime), cos(log(float(i+1))*S*iTime));
    }

    float hyp = 0.0f;
    for (int i = 0; i < N; i++) {
        float dx = mid.x-points[i].x, dy = mid.y-points[i].y;
        hyp += W/(dx*dx+dy*dy);
    }
    if (hyp < 1.0f) {
        col *= sqrt(hyp)*hyp;
    }

    fragColor = vec4(col, 1.0);
}
