bool inside(vec2 pos)
{
  vec3 greenscreen = vec3(0.05, 0.64, 0.15);
  vec3 col = texture(iChannel0, pos).rgb;
  return length(col - greenscreen) > 0.1;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    float divisor = 4.0;
    vec2 coord = floor(fragCoord / divisor);
    vec2 res = iResolution.xy / divisor;

    vec2 uv = coord/res;
    vec2 right = vec2(1.0, 0.0);
    vec2 down = vec2(0.0, 1.0);

    vec3 x = vec3(0.8, 0.8 * uv.y, 0.8);
    if (!inside(uv))
    {
        if (inside((coord + right) / res) ||
            inside((coord + down) / res) ||
            inside((coord - right) / res) ||
            inside((coord - down) / res))
            x = vec3(1.0);
        else
        {
            float time = iTime + 2.5 * sin(iTime);
            vec2 primary = vec2(sin(time), cos(time));
            float p = dot(normalize(fragCoord/iResolution.xy - vec2(0.8, -0.2)), primary);
            x = vec3(0.05 + 0.3 * step(0.6 + 0.3 * sin(iTime), p),
                     0.24 + 0.58 * step(cos(iTime), p),
                     0.45 + 0.43 * step(p, -0.8));
        }
    }
    // Output to screen
    fragColor = vec4(x,1.0);
}
