#define H(h)(cos((h)*6.3+vec3(0,23,21))*.5+.5)
void mainImage(out vec4 O, vec2 C)
{
    O=vec4(0);
    vec3 p,r=iResolution,
    d=normalize(vec3((C-.5*r.xy)/r.y,1));  
    for(float i=0.,s,e,g=0.;
        ++i<99.;
        e<.001?O.rgb+=mix(vec3(1),H(log(s)*.2),.7)*.1*exp(-i*i*e):r
    )
    {
        p=g*d;
        p+=vec3(
            fract(sin(floor(iTime))*15.),
            fract(sin(floor(iTime*.8))*12.),
            iTime*.05
        );
        vec4 q=vec4(p,.01);
        s=2.;
        q=sin(q*20.);
        for(int i=0;i++<6;q=abs(q)*e)
            q=abs(q-1.5)-1.2,
            s*=e=2.3/clamp(dot(q,q),.5,1.2);
        g+=e=length(q.zw)/s;
    }
}
