// draw arrows 
// 2D vector field visualization by Morgan McGuire, from @morgan3d, http://casual-effects.com
const float PI = 3.1415927;
const int   ARROW_V_STYLE = 1;
const int   ARROW_LINE_STYLE = 2;
const int   ARROW_STYLE = ARROW_LINE_STYLE;
#if CUR_ARR
    const float ARROW_TILE_SIZE = 17.;
    const float ARROW_HEAD_ANGLE = 90.0 * PI / 180.0;
    const float ARROW_HEAD_LENGTH = ARROW_TILE_SIZE / 7.;
    const float ARROW_SHAFT_THICKNESS = 2.;
#else
    const float ARROW_TILE_SIZE = 10.;
    const float ARROW_HEAD_ANGLE = 120.0 * PI / 180.0;
    const float ARROW_HEAD_LENGTH = ARROW_TILE_SIZE / 10.;
    const float ARROW_SHAFT_THICKNESS = 1.;
#endif

vec2 arrowTileCenterCoord(vec2 pos) {
	return (floor(pos / ARROW_TILE_SIZE) + 0.5) * ARROW_TILE_SIZE;
}
float arrow(vec2 p, vec2 v) {
	p -= arrowTileCenterCoord(p);
    float mag_v = length(v), mag_p = length(p);
	if (mag_v > .5) {
		vec2 dir_p = p / mag_p, dir_v = v / mag_v;
		mag_v = clamp(mag_v, 0.5, ARROW_TILE_SIZE / 3.);
		v = dir_v * mag_v;
		float dist;		
		if (ARROW_STYLE == ARROW_LINE_STYLE) {
			dist = max(ARROW_SHAFT_THICKNESS / 4.0 - 
                    max(abs(dot(p, vec2(dir_v.y, -dir_v.x))),
                        abs(dot(p, dir_v)) - mag_v + ARROW_HEAD_LENGTH / 2.0), 
                        min(0.0, dot(v - p, dir_v) - cos(ARROW_HEAD_ANGLE / 2.0) * length(v - p)) * 2.0 +
                        min(0.0, dot(p, dir_v) + ARROW_HEAD_LENGTH - mag_v));
		} else {
			dist = min(0.0, mag_v - mag_p) * 2.0 +
				   min(0.0, dot(normalize(v - p), dir_v) - cos(ARROW_HEAD_ANGLE / 2.0)) * 2.0 * length(v - p) +
				   min(0.0, dot(p, dir_v) + 1.0) +
				   min(0.0, cos(ARROW_HEAD_ANGLE / 2.0) - dot(normalize(v * 0.33 - p), dir_v)) * mag_v * 0.8;
		}

		return clamp(0.6 + dist, 0.0, 1.0);
	} else {
		return max(0.0, 1.2 - mag_p);
	}
}

void mainImage(out vec4 fragColor,in vec2 fragCoord)
{
    ivec2 idx = ivec2(fragCoord * 0.5);

    float rho = RHO(iChannel1, idx);
    float F_len = rho>0.001? F_Len(iChannel0, idx)*min(rho*3.,1.)*8.:0.;
    //float v_len = rho>0.0001? V_Len(iChannel0, idx)*min(rho*10.,1.)*3.:0.;
    vec2 v = vec2(V_x(iChannel0, idx),V_y(iChannel0, idx));

    //float v_len = length(v);
    //v_len = rho>0.001? v_len*min(rho*10.,1.)*3.:0.;
    float wall = Wall(iChannel2, idx);
    ivec2 size = ivec2(iResolution*0.5);
    ivec2 center = ivec2(size.x/2,size.y/3*2);
    float tube = (step(length(vec2(center-idx)),10.5) - step(length(vec2(center-idx)),5.6));

    wall += rho<.5?tube*.6:0.;

    float flag_v = texelFetch( iChannel3, ivec2(KEY_V,2), 0 ).x<0.5?0.:1.;
    float flag_f = texelFetch( iChannel3, ivec2(KEY_F,2), 0 ).x<0.5?0.:1.;

    vec4 color = vec4(.1,.1,.1,1.);
    color += vec4(vec3(.1,.1,.65)*rho,0.);                   // display fluid (density)
    color += vec4(0,F_len,0,0)*flag_f;                       // display force (free surface)
    color += vec4(.28,.28,.25,0.)*wall;                      // display wall
    //color += vec4(hue2rgb(v_len)*v_len*0.8,1)*flag_v;      // display velocity (color)
    // display velocity
    ivec2 idx_tile = ivec2(arrowTileCenterCoord(fragCoord) * 0.5);
    vec2 v_tile = vec2(V_x(iChannel0, idx_tile),V_y(iChannel0, idx_tile));
    color -= vec4(-5.,-5.,5.,0.) * arrow(fragCoord.xy,
#if CUR_ARR
    v
#else
    v_tile
#endif
    * ARROW_TILE_SIZE * 10.) * flag_v*
    step(.1,RHO(iChannel1, idx_tile)) *
#if CUR_ARR
    step(F_Len(iChannel0, idx),.5) *
#endif    
    step(wall, .1); //step(Wall(iChannel2, idx_tile),.1);

    fragColor=color;
}

// collision step

// kernal
const ivec2 off[25] = ivec2[25](
    ivec2( 0, 0 ),
    ivec2( 1, 0 ),ivec2( 0, -1 ),ivec2( -1, 0 ),ivec2( 0, 1 ),
    ivec2( 1, -1 ), ivec2( -1, -1 ), ivec2( -1, 1 ), ivec2( 1, 1 ),
    ivec2( 2, 0 ), ivec2( 0, -2 ), ivec2( -2, 0 ), ivec2( 0, 2 ),
    ivec2( 1, -2 ), ivec2( -1, -2 ), ivec2( -1, 2 ), ivec2( 1, 2 ),
    ivec2( 2, -1 ), ivec2( -2, -1 ), ivec2( -2, 1 ), ivec2( 2, 1 ),
    ivec2( 2, -2 ), ivec2( -2, -2 ), ivec2( -2, 2 ), ivec2( 2, 2 )
);
const float w[9] = float[9](
    0., 1. / 21., 4. / 45., 0., 1. / 60., 2. / 315., 0., 0., 1. / 5040.
);

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 idx = ivec2(fragCoord*0.5);
    ivec2 size = ivec2(iResolution*0.5);

    if(is_wall(iChannel2,idx,size))
    {
        discard;
    }

    // density distribution
    float f0,f1,f2,f3,f4,f5,f6,f7,f8;
    // total density
    float rho;
    float psi;

    // get f0~8 from BufferB
    f0 = f0(iChannel1, idx);
    f1 = f1(iChannel1, idx);
    f2 = f2(iChannel1, idx);
    f3 = f3(iChannel1, idx);
    f4 = f4(iChannel1, idx);
    f5 = f5(iChannel1, idx);
    f6 = f6(iChannel1, idx);
    f7 = f7(iChannel1, idx);
    f8 = f8(iChannel1, idx);
    psi = PSI(iChannel1, idx);
    // density
    rho = f0 + f1 + f2 + f3 + f4 + f5 + f6 + f7 + f8;
    // velocity
    vec2 v1 = (1./(rho+ 1e-20)) * vec2((f1 - f2 + f5 - f6 - f7 + f8),(f3 - f4 + f5 - f6 + f7 - f8));

    // calculate external force (surface tension)
    // Shan-Chen-type model, https://journals.aps.org/pre/pdf/10.1103/PhysRevE.47.1815
    vec2 F = vec2(0, 0);
    for(int i = 1; i < 25; i++) {
        ivec2 offset = off[i];
        ivec2 idx2 = idx + offset;
        float psi_self = PSI(iChannel1, idx2);
        float psi_wall = is_wall(iChannel2, idx2, size) ? 1. : 0.;
        F += (G_self * psi_self + G_wall * psi_wall) * vec2(offset) * w[offset.x * offset.x + offset.y * offset.y];
    }

    F *= max(psi,.95);
    vec2 a = F * min(1./(rho+ 1e-20), 100.) + g; //gravity
    vec2 v2 = v1 + a; // velocity2

    // velocity limit
    const float max_speed = 0.57735027;
    if(length(v1) > max_speed) {
        v1 = normalize(v1) * max_speed;
    }
    if(length(v2) > max_speed) {
        v2 = normalize(v2) * max_speed;
    }

    // f_eq(i,v)
    float sq_term = - 1.5 * (v1.x * v1.x + v1.y * v1.y);
    float f0eq = 4. / 9. * rho * (1. + sq_term);
    float f1eq = 1. / 9. * rho * (1. + 3. * v1.x + 4.5 * v1.x * v1.x + sq_term);
    float f2eq = 1. / 9. * rho * (1. - 3. * v1.x + 4.5 * v1.x * v1.x + sq_term);
    float f3eq = 1. / 9. * rho * (1. + 3. * v1.y + 4.5 * v1.y * v1.y + sq_term);
    float f4eq = 1. / 9. * rho * (1. - 3. * v1.y + 4.5 * v1.y * v1.y + sq_term);
    float f5eq = 1. / 36. * rho * (1. + 3. * (v1.x + v1.y) + 4.5 * (v1.x + v1.y) * (v1.x + v1.y) + sq_term);
    float f6eq = 1. / 36. * rho * (1. - 3. * (v1.x + v1.y) + 4.5 * (v1.x + v1.y) * (v1.x + v1.y) + sq_term);
    float f7eq = 1. / 36. * rho * (1. + 3. * (- v1.x + v1.y) + 4.5 * (- v1.x + v1.y) * (- v1.x + v1.y) + sq_term);
    float f8eq = 1. / 36. * rho * (1. - 3. * (- v1.x + v1.y) + 4.5 * (- v1.x + v1.y) * (- v1.x + v1.y) + sq_term);

    // f_eq(i,v2)
    float sq_term2 = - 1.5 * (v2.x * v2.x + v2.y * v2.y);
    float f0eq2 = 4. / 9. * rho * (1. + sq_term2);
    float f1eq2 = 1. / 9. * rho * (1. + 3. * v2.x + 4.5 * v2.x * v2.x + sq_term2);
    float f2eq2 = 1. / 9. * rho * (1. - 3. * v2.x + 4.5 * v2.x * v2.x + sq_term2);
    float f3eq2 = 1. / 9. * rho * (1. + 3. * v2.y + 4.5 * v2.y * v2.y + sq_term2);
    float f4eq2 = 1. / 9. * rho * (1. - 3. * v2.y + 4.5 * v2.y * v2.y + sq_term2);
    float f5eq2 = 1. / 36. * rho * (1. + 3. * (v2.x + v2.y) + 4.5 * (v2.x + v2.y) * (v2.x + v2.y) + sq_term2);
    float f6eq2 = 1. / 36. * rho * (1. - 3. * (v2.x + v2.y) + 4.5 * (v2.x + v2.y) * (v2.x + v2.y) + sq_term2);
    float f7eq2 = 1. / 36. * rho * (1. + 3. * (- v2.x + v2.y) + 4.5 * (- v2.x + v2.y) * (- v2.x + v2.y) + sq_term2);
    float f8eq2 = 1. / 36. * rho * (1. - 3. * (- v2.x + v2.y) + 4.5 * (- v2.x + v2.y) * (- v2.x + v2.y) + sq_term2);

    // f_new(i) = (1-k)*f(i) + (k-1)*f_eq(i,v1) + f_eq(i,v2)
    f0 = (1. - k) * f0 + (k - 1.) * f0eq + f0eq2;
    f1 = (1. - k) * f1 + (k - 1.) * f1eq + f1eq2;
    f2 = (1. - k) * f2 + (k - 1.) * f2eq + f2eq2;
    f3 = (1. - k) * f3 + (k - 1.) * f3eq + f3eq2;
    f4 = (1. - k) * f4 + (k - 1.) * f4eq + f4eq2;
    f5 = (1. - k) * f5 + (k - 1.) * f5eq + f5eq2;
    f6 = (1. - k) * f6 + (k - 1.) * f6eq + f6eq2;
    f7 = (1. - k) * f7 + (k - 1.) * f7eq + f7eq2;
    f8 = (1. - k) * f8 + (k - 1.) * f8eq + f8eq2;

    // save
    ivec2 off = ivec2(int(fragCoord.x) - 2*idx.x,int(fragCoord.y) - 2*idx.y);
    fragColor = save_color(off,vec4[4](
    vec4(f0,f1,f2,1.),
    vec4(f3,f4,f5,1.),
    vec4(f6,f7,f8,1.),
    vec4(rho,length(F),((v1+v2)*0.5).xy)));
}

// streaming step
#define SizeChanged() (texelFetch(iChannel2, ivec2(0,0),0).r)

#define PI 3.1415926535898
float _atan1(float x)
{
    x-=1.;
    return .25*PI+.5*x-.25*x*x;
}

void mainImage( out vec4 fragColor, in vec2 fragCoord ){

    ivec2 idx = ivec2(fragCoord*0.5);
    ivec2 size = ivec2(iResolution*0.5);

    float f0, f1, f2, f3, f4, f5, f6, f7, f8;

    if(!is_wall(iChannel2,idx,size)&&SizeChanged()<0.5)
    {
        ivec2 idx2;
        ivec2 center = ivec2(size.x/2,size.y/3*2);

        if((f0 < 0.1) && length(vec2(center-idx))<6.) // water source
        {
            f4 = 0.5; //↓
            //f1 = 1.; //→
        }
        else // streaming and handling boundary condition 
        {
            f0 = f0(iChannel0, idx);
            idx2 = idx + ivec2(-1, 0);
            f1 = is_wall(iChannel2,idx2, size) ? f2(iChannel0, idx) : f1(iChannel0, idx2);
            idx2 = idx + ivec2(1, 0);
            f2 = is_wall(iChannel2,idx2, size) ? f1(iChannel0, idx) : f2(iChannel0, idx2);
            idx2 = idx + ivec2(0, -1);
            f3 = is_wall(iChannel2,idx2, size) ? f4(iChannel0, idx) : f3(iChannel0, idx2);
            idx2 = idx + ivec2(0, 1);
            f4 = is_wall(iChannel2,idx2, size) ? f3(iChannel0, idx) : f4(iChannel0, idx2);
            idx2 = idx + ivec2(-1, -1);
            f5 = is_wall(iChannel2,idx2, size) ? f6(iChannel0, idx) : f5(iChannel0, idx2);
            idx2 = idx + ivec2(1, 1);
            f6 = is_wall(iChannel2,idx2, size) ? f5(iChannel0, idx) : f6(iChannel0, idx2);
            idx2 = idx + ivec2(1, -1);
            f7 = is_wall(iChannel2,idx2, size) ? f8(iChannel0, idx) : f7(iChannel0, idx2);
            idx2 = idx + ivec2(-1, 1);
            f8 = is_wall(iChannel2,idx2, size) ? f7(iChannel0, idx) : f8(iChannel0, idx2);
        }
    }

    const float rho0 = 1.;
    float rho = f0+f1+f2+f3+f4+f5+f6+f7+f8;
    float r = rho/rho0;
    float p = max(r-1.,0.);

    /* pseudo-potential */
    float psi = sign(r) * rho0 * clamp((
    /* surface tension */ 
    1. - exp(-abs(r))
    /* pressure, it makes the fluid more incompressible*/
    - (1.5*p*sqrt(p))), //-p),
    /* clamp */
    -100.,1.);

    // save
    ivec2 off = ivec2(int(fragCoord.x) - 2*idx.x,int(fragCoord.y) - 2*idx.y);
    fragColor = save_color(off,vec4[4](
    vec4(f0,f1,f2,1.),
    vec4(f3,f4,f5,1.),
    vec4(f6,f7,f8,1.),
    vec4(rho,0,0,psi)));
}

// draw wall

#define IsInBox(box, idx) (idx.x > box.x && idx.x < box.y && idx.y > box.z && idx.y < box.w)
#define IsInCircle(o,r,idx) (length(o-vec2(idx))<r)
//#define KEY_SPACE 32
#define KEY_E 69

#define Resolution() (texelFetch(iChannel2, ivec2(0,0), 0).zwx)

void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    ivec2 idx = ivec2(fragCoord*0.5);
    ivec2 size = ivec2(iResolution.xy*0.5);
    vec3 resolution_old = Resolution();
    vec4 data = texelFetch(iChannel2,idx*2,0);

    if(idx.x==0 && idx.y==0){
        if(any(notEqual(resolution_old.xy,iResolution.xy))){
            data = vec4(1., data.y, iResolution.xy);
        }else{
            data = vec4(0., data.y, iResolution.xy);
        }
    }
    else if(resolution_old.z>0.5)
    {
        ivec4 box1 = ivec4(size.x/2-10,size.x/2+90,size.y/3-20,size.y/3-10);
        ivec4 box2 = box1-size.y/8;
        ivec4 box3 = box2 + ivec4(100,100,-size.y/16,-size.y/16);
        ivec4 box4 = ivec4(size.x/3+size.x/120,size.x/3+size.x/120+10,6,60);
        ivec4 box5 = box4 + ivec4(size.x/3,size.x/3,-20,20);
        ivec4 box6 = box4 - ivec4(25,25,10,20);
        if(IsInBox(box1,idx)) {
           data = vec4(1.);
        }
        else if(IsInBox(box2,idx)) {
           data = vec4(1.);
        }
        else if(IsInBox(box3,idx)) {
           data = vec4(1.);
        }        
        else if(IsInBox(box4,idx)) {
           data = vec4(1.);
        }            
        else if(IsInBox(box5,idx)) {
           data = vec4(1.);
        } 
        else if(IsInBox(box6,idx)) {
           data = vec4(1.);
        }
        else{
           data = vec4(0.);
        }
    }
    else
    {
        // input
        if(iMouse.z>0. && IsInCircle(iMouse.xy/2.,5.,idx))
        {
            data.xy = vec2(texelFetch( iChannel3, ivec2(KEY_E,2), 0 ).x<0.9?1.:0.);
        }
    }

    // save
    fragColor = data;
}
